declare module "@/lib/i18n" {
  /**
   * Type-safe translation hook (Client Component)
   * 
   * @template NS - The namespace to use
   * @param namespace - The namespace string
   * @returns Translation utilities with type-safe keys
   * 
   * @example
   * ```tsx
   * const { t } = useTranslation<"home">("home");
   * t("title");  // âœ… OK
   * t("typo");   // âŒ Compile error!
   * ```
   */
  // Helper type to extract variable names from keys
  type ExtractVariables<K> = 
    K extends keyof (CommonKeyVariables) ? 
      (CommonKeyVariables)[K] : 
      never;

  export function useTranslation<NS extends TranslationNamespace>(
    namespace: NS
  ): {
    t: <K extends TranslationKeys[NS]>(
      key: K,
      ...args: ExtractVariables<K> extends never
        ? [variables?: Record<string, string | number>, styles?: Record<string, React.CSSProperties>]
        : [variables: Record<ExtractVariables<K>, string | number>, styles?: Record<string, React.CSSProperties>]
    ) => ExtractVariables<K> extends never ? string : (typeof args extends [any, any] ? React.ReactElement : string);
    currentLanguage: string;
    isReady: boolean;
  };
}

declare module "@/lib/i18n/server" {
  /**
   * Type-safe translation function (Server Component)
   * 
   * @template NS - The namespace to use
   * @param namespace - The namespace string
   * @returns Translation utilities with type-safe keys
   * 
   * @example
   * ```tsx
   * const { t } = await getTranslation<"home">("home");
   * t("title");  // âœ… OK
   * t("typo");   // âŒ Compile error!
   * ```
   */
  export function getTranslation<NS extends TranslationNamespace>(
    namespace: NS,
    options?: {
      localesDir?: string;
      cookieName?: string;
      defaultLanguage?: string;
      availableLanguages?: string[];
    }
  ): Promise<{
    t: (key: TranslationKeys[NS]) => string;
    language: string;
    translations: Record<string, Record<string, string>>;
    dict: Record<string, string>;
  }>;
}
